import React from 'react';
import Slide from '../Slide/Slide';
import Product from './ProductModel/Product';
import '../../style/global.css';
import './HotItem.css';
const HotItem: React.FC = () => {


  const products = [
    {
      imageUrl: 'path/to/image1.jpg',
      sellerName: '판매자 브랜드명',
